import useInput from "../hooks/use-input";
const isNotEmpty = (value) => value.trim() !== "";
const isEmail = (value) => value.include("@");
const BasicForm = (props) => {
  const {
    value: firstNameValue,
    isValid: firstNameIsValid,
    valueChageHandler: firstNameChangeHandler,
    inputBlurHandler: firstNameBlueHandler,
    reset: resetFirstName,
  } = useInput(isNotEmpty);

  const {
    value: lastNameValue,
    isValid: lastNameIsValid,
    valueChageHandler: lastNameChangeHandler,
    inputBlurHandler: lastNameBlueHandler,
    reset: resetLastName,
  } = useInput(isNotEmpty);
const
 { value: emailValue,
    isValid: emailIsValid,
    valueChageHandler: emailChangeHandler,
    inputBlurHandler: emailBlueHandler,
    reset: resetEmail} = useInput(isEmail);



  return (
    <form>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">First Name</label>
          <input type="text" id="name" value ={firstNameValue} onChange ={firstNameChangeHandler} onBlur={firstNameBlueHandler}/>
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name"value ={lastNameValue} onChange ={lastNameChangeHandler} onBlur={lastNameBlueHandler} />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name" />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
